import React from 'react';
import './App.css';
import Item from './Item.js';
import Grid from '@material-ui/core/Grid';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import ButtonGroup from 'react-bootstrap/ButtonGroup';



class Catalog extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        league: "All",
        style: "All",
    }
  }

  handleFilterLeague = (league) => {
    this.setState({league: league});
  }

  handleFilterStyle = (style) => {
    this.setState({style: style});
  }

  checkFilters = (item) => {
      if (this.state.league === "All" && this.state.style === "All") {
        return true;
      } else if (this.state.league === "All" && this.state.style === item.style) {
        return true;
      } else if (this.state.league === item.league && this.state.style === "All") {
        return true;
      } else if (this.state.league === item.league && this.state.style === item.style) {
        return true;
      } else {
        return false;
      }
  }

  render() {
    let filteredItems = this.props.list.filter(this.checkFilters);
    return (
      <div>
        <DropdownButton variant={'danger'} id="dropdown-item-button" title="League">
            <Dropdown.Item as="button" onClick={() => this.handleFilterLeague("All")}>All</Dropdown.Item>
            <Dropdown.Item as="button" onClick={() => this.handleFilterLeague("NBA")}>NBA</Dropdown.Item>
            <Dropdown.Item as="button" onClick={() => this.handleFilterLeague("MLB")}>MLB</Dropdown.Item>
        </DropdownButton>
        <DropdownButton variant={'warning'} id="dropdown-item-button" title="Style">
            <Dropdown.Item as="button">All</Dropdown.Item>
            <Dropdown.Item as="button">Snapback</Dropdown.Item>
            <Dropdown.Item as="button">Ball Cap</Dropdown.Item>
        </DropdownButton>
        <Grid container spacing={2}>
          {filteredItems.map(item => 
          <Grid item xs={4} sm={3}>
            <Item 
                name = {item.name}
                league = {item.league}
                style = {item.style}
                price = {item.price}
                image = {item.image}/>
          </Grid>
            )}
        </Grid>
      </div>
    );
  }
}

export default Catalog;
