import React from 'react';
import './App.css';
import Item from './Item.js';
import Grid from '@material-ui/core/Grid';
import Dropdown from 'react-bootstrap/Dropdown';


class Catalog extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        league: "All",
        style: "All",
    }
  }

  handleFilterLeague = (league) => {
    this.setState({league: league});
  }

  handleFilterStyle = (style) => {
    this.setState({style: style});
  }

  checkFilters = (item) => {
      if (this.state.league === "All" && this.state.style === "All") {
        return true;
      } else if (this.state.league === "All" && this.state.style === item.style) {
        return true;
      } else if (this.state.league === item.league && this.state.style === "All") {
        return true;
      } else if (this.state.league === item.league && this.state.style === item.style) {
        return true;
      } else {
        return false;
      }
  }

  render() {
    let filteredItems = this.props.list.filter(this.checkFilters);
    return (
      <div>
          <DropdownButton id="dropdown-item-button" title="Dropdown button">
  <Dropdown.ItemText>Dropdown item text</Dropdown.ItemText>
  <Dropdown.Item as="button">Action</Dropdown.Item>
  <Dropdown.Item as="button">Another action</Dropdown.Item>
  <Dropdown.Item as="button">Something else</Dropdown.Item>
</DropdownButton>
          <div>
          <Dropdown.Menu show>
          <Dropdown.Header>League</Dropdown.Header>
          <Dropdown.Item eventKey="2">All</Dropdown.Item>
          <Dropdown.Item eventKey="3">NBA</Dropdown.Item>
          <Dropdown.Item eventKey="4">MLB</Dropdown.Item>
        </Dropdown.Menu>
        <Dropdown.Menu show>
          <Dropdown.Header>Style</Dropdown.Header>
          <Dropdown.Item eventKey="2">All</Dropdown.Item>
          <Dropdown.Item eventKey="3">Snapback</Dropdown.Item>
          <Dropdown.Item eventKey="4">Ball Cap</Dropdown.Item>
        </Dropdown.Menu>
          </div>


        <Grid container spacing={2}>
          {filteredItems.map(item => 
          <Grid item xs={4} sm={3}>
            <Item 
                name = {item.name}
                league = {item.league}
                style = {item.style}
                price = {item.price}
                image = {item.image}/>
          </Grid>
            )}
        </Grid>
      </div>
    );
  }
}

export default Catalog;
