import React from 'react';
import './App.css';
import Item from './Item.js';
import Cart from './Cart.js';
import Grid from '@material-ui/core/Grid';
import Table from '@material-ui/core/Table';
import TableHead from '@material-ui/core/TableHead';
import TableBody from '@material-ui/core/TableBody';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableHead';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Paper from '@material-ui/core/Paper';
import TableContainer from '@material-ui/core/TableContainer';

class Catalog extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        league: "All",
        style: "All",
        sort: "None",
        cartList: [],
        total: 0,
    }
  }

  handleFilterLeague = (league) => {
    this.setState({league: league});
  }

  handleFilterStyle = (style) => {
    this.setState({style: style});
  }

  checkFilters = (item) => {
      if (this.state.league === "All" && this.state.style === "All") {
        return true;
      } else if (this.state.league === "All" && this.state.style === item.style) {
        return true;
      } else if (this.state.league === item.league && this.state.style === "All") {
        return true;
      } else if (this.state.league === item.league && this.state.style === item.style) {
        return true;
      } else {
        return false;
      }
  }

  handleSort = (sort) => {
    this.setState({sort: sort});
  }

  checkSort = (list) => {
      if (this.state.sort === "None") {
        return list;
      } else if (this.state.sort === "Low") {
        list.sort((a,b)=> a.price - b.price);
        return list;
      } else if (this.state.sort === "High") {
        list.sort((a,b)=> b.price - a.price);
        return list;
      }
  }


  handleAddToCart = (item) => {
    let cart = this.state.cartList;
    let itemInCart = cart.some(i => {return i.name === item.name});
    if (itemInCart) {
        let i = cart.findIndex(curr => {return curr.name === item.name});
        cart[i].count = cart[i].count + item.count;
    } else {
        cart.push(item);
    }
    this.setState({
        cartList: cart
    });
  }

  removeAll = (name) => {
    let cart = this.state.cartList;
    let removed = cart.filter(product => product.name !== name);
    this.setState({
        cartList: removed
    });
  }

  calculateTotalPrice() {
      let cart = this.state.cartList;
      let totalPrice = 0;
      for (let i=0;i<cart.length;i++){
          totalPrice += (parseInt(cart[i].price) * cart[i].count);
      }
      this.setState({
        total: totalPrice
    });
  }

  render() {
    let filteredItems = this.props.list.filter(this.checkFilters);
    let items = this.checkSort(filteredItems);
    let cartItems = this.state.cartList;
    return (
      <div>
        <DropdownButton variant={'danger'} id="dropdown-item-button" title="League">
            <Dropdown.Item as="button" active={this.state.league === "All" ? true : false} onClick={() => this.handleFilterLeague("All")}>All</Dropdown.Item>
            <Dropdown.Item as="button" active={this.state.league === "NBA" ? true : false} onClick={() => this.handleFilterLeague("NBA")}>NBA</Dropdown.Item>
            <Dropdown.Item as="button" active={this.state.league === "MLB" ? true : false} onClick={() => this.handleFilterLeague("MLB")}>MLB</Dropdown.Item>
        </DropdownButton>
        <DropdownButton variant={'warning'} id="dropdown-item-button" title="Style">
            <Dropdown.Item as="button" active={this.state.style === "All" ? true : false} onClick={() => this.handleFilterStyle("All")}>All</Dropdown.Item>
            <Dropdown.Item as="button" active={this.state.style === "Snapback" ? true : false} onClick={() => this.handleFilterStyle("Snapback")}>Snapback</Dropdown.Item>
            <Dropdown.Item as="button" active={this.state.style === "Ball Cap" ? true : false} onClick={() => this.handleFilterStyle("Ball Cap")}>Ball Cap</Dropdown.Item>
        </DropdownButton>
        <DropdownButton id="dropdown-item-button" title="Sort By">
            <Dropdown.Item as="button" active={this.state.sort === "None" ? true : false} onClick={() => this.handleSort("None")}>Featured</Dropdown.Item>
            <Dropdown.Item as="button" active={this.state.sort === "Low" ? true : false} onClick={() => this.handleSort("Low")}>Price Low to High</Dropdown.Item>
            <Dropdown.Item as="button" active={this.state.sort === "High" ? true : false} onClick={() => this.handleSort("High")}>Price High to Low</Dropdown.Item>
        </DropdownButton>
        <Grid container spacing={2}>
          {items.map(item => 
          <Grid item xs={4} sm={3}>
            <Item 
                name = {item.name}
                league = {item.league}
                style = {item.style}
                price = {item.price}
                image = {item.image}
                handleAddToCart = {this.handleAddToCart}/>
          </Grid>
            )}
        </Grid>
        <div><h3>My Cart</h3></div>
        <div>
        {/* <TableContainer component={Paper}>
      <Table aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Dessert (100g serving)</TableCell>
            <TableCell align="right">Calories</TableCell>
            <TableCell align="right">Fat&nbsp;(g)</TableCell>
            <TableCell align="right">Carbs&nbsp;(g)</TableCell>
            <TableCell align="right">Protein&nbsp;(g)</TableCell>
          </TableRow>
        </TableHead>

      </Table>
    </TableContainer> */}
        {/* <TableContainer component={Paper}>
        <Table className={"table"} aria-label="simple table">
            <TableHead>
                <TableRow>
                <TableCell>Image</TableCell>
                <TableCell>Item</TableCell>
                <TableCell>Price</TableCell>
                <TableCell>Quantity</TableCell>
                <TableCell>Remove</TableCell>
                </TableRow>
            </TableHead>
            <TableBody>
            {cartItems.map(item => 
                <Cart
                    name={item.name}
                    price={item.price}
                    image={item.image}
                    count={item.count}
                    handleRemove={this.removeAll}/>
                )}
            </TableBody>
        </Table>
        </TableContainer> */}




        {cartItems.map(item => 
                <Cart
                    name={item.name}
                    price={item.price}
                    image={item.image}
                    // count={item.count}
                    handleRemove={this.removeAll}/>
                )}
        </div>
        <div>
            <h4>Order Summary</h4>
            <h5>Total ${this.state.total}</h5>
        </div>

            {/* <Cart cartList={this.state.cartList} total={this.state.total} handleRemove ={this.removeAll}/> */}
      </div>
    );
  }
}

export default Catalog;
